            j = kebox(ix,iy)
            if (j.lt.0) goto 2624
            call drawtasto(j+10)
            call ClearMessage
            nbox0  = 1
            nboxn  = nbox
            ColTast = 3
 2625       if(j.eq.(nbox-10)+ 24) then
               call hide_mouse_cursor        ! More ...
               call ClearMessage
               iz = 10
               goto 2623
            else if(j.eq.(nbox-10) + 29) then
               call hide_mouse_cursor        ! More ...
               call ClearMessage
               iz = 9
               goto 2623
