c--    9  : draw string (iiz chars) in position iix,iiy
      else if (ind.eq.8) then
      else if (ind.eq.9) then
         iarrow = 0
         if (ix.lt.0) then
            iarrow = 1
            ix = abs(ix)
            ix1 = 0
            iy1 = 21
            ix2 = 80
            iy2 = kscry
            icol = 7 !white
#if defined(_DOS_) 
            call clear_screen_area@(ix1,iy1,ix2,iy2,icol)
#endif
#if defined(__LIN__) || defined(__GIL__) || defined(__MAC__) 
            call clear_screen_area(ix1,iy1,ix2,iy2,icol)
