         res = ELLIPSE($GFILLINTERIOR,ix-iz1,iy-iz,ix+iz1,iy+iz)
#endif
c--   12  : set foreground color to iicol
      else if (ind.eq.12)then
         icol = iicol
         color=icol
c--   13  : draw iiz-points polygon using color iicol
      else if (ind.eq.13)then
         do i=1,iz
