      subroutine DrawMenu(Iz)
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
      USE DFLIB
      USE DFWIN
      INTEGER*2 RES
      logical*4 status
      type (windowconfig)wc
      type (qwinfo)win
      TYPE (xycoord) xy
#endif
      character*14 strbox, strapp
      character *80 Titolo,messaggio,MessB
      COMMON /Contr/ NF,iFirstTime,Titolo,Ratio,IfCont,IfEdge,IfScale,
     *               kplty,radii(8),IfScaleB
      common /cmenu/ xbox(78),ybox(78),lbox(78),strbox(78),nbox,nboxt,
     +               nbox0,nboxn,ColTast,strapp(11)
      common /screen/ kscreen,jy,ivd,kscrx,kscry,jscreen,kprog,ifMouse
      integer*2 ix,iy,ix1,iy1,icol,widb,heib,Iz,iiz,iy2
      real*4 size,rot,ita
      integer*2 font,iwid,ihei,ColTast
c
      ColTast = 3
      if ( Iz .eq. 0 )  then
         nbox0  = 1
         nboxn  = nbox
      elseif (Iz.eq.-1) then
         nbox0  = 10
         nboxn  = 10
      elseif (Iz.eq.2) then
         nbox0  = nbox + 1
         nboxn  = nbox + 5
         ColTast = 7
      elseif (Iz.eq.3) then
         nbox0  = nbox + 6
         nboxn  = nbox + 7
         ColTast = 7
      elseif (Iz.eq.4) then
         nbox0  = nbox + 8
         nboxn  = nbox + 12
         ColTast = 7
      elseif (Iz.eq.5) then
         nbox0  = nbox + 13
         nboxn  = nbox + 17
         ColTast = 7
      elseif (Iz.eq.6) then
         nbox0  = nbox + 12
         nboxn  = nbox + 12
         ColTast = 7
      elseif (Iz.eq.7) then
         nbox0  = nbox + 11
         nboxn  = nbox + 12
         ColTast = 7
      elseif (Iz.eq.8) then
         nbox0  = nbox + 18
         nboxn  = nbox + 20
         ColTast = 7
      elseif (Iz.eq.9) then
         nbox0  = nbox + 21
         nboxn  = nbox + 25
         ColTast = 7
      elseif (Iz.eq.10) then
         nbox0  = nbox + 26
         nboxn  = nbox + 30
         ColTast = 7
      elseif (Iz.eq.11) then
         nbox0  = nbox + 35
         nboxn  = nbox + 37
         ColTast = 7
      elseif (Iz.eq.12) then
         nbox0  = nbox + 31
         nboxn  = nbox + 34
         ColTast = 7
      elseif (Iz.eq.13) then
         nbox0  = nbox + 33
         nboxn  = nbox + 34
         ColTast = 7
      endif
c
c-- scrive il titolo
c
      if (Iz.eq.0) then
         ix = 81
         iy = 0
         ix1 = kscrx
         iy1 = 20
         iwid = kscrx-ix
         ihei = iy1
cdjw         icol = 3 !teal
         icol = 7 !white
#if defined(_DOS_) 
         call fill_rectangle@(ix,iy,ix1,iy1,icol)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
         call fill_rectangle(ix,iy,ix1,iy1,icol)
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
         RES= SETCOLOR ( icol )
         RES= RECTANGLE ($GFILLINTERIOR,ix,iy,ix1,iy1)
#endif

         kact = 1
         call Tasto (ix,iy,iwid,ihei,kact,1)
cdjw         icol = 4 !dark red
         icol = 0 ! black
         ix = 84
         iy = 4
         kkprog = mod ( kprog , 10 )
         if (kkprog.eq.1) then
            MessB = ' SIR92 : '
            lm = 9
         else
            MessB = ' SIRPOW.92 : '
            lm = 13
         end if
         messaggio = MessB(1:lm)//Titolo
         iiz = Lung(messaggio)
#if defined(_DOS_) 
         call draw_text@(messaggio(1:iiz),ix,iy,icol)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
         call draw_text(messaggio(1:iiz),ix,iy,icol)
