         call ClearMessage
      end if
c
c-- disegna i rettangoli per i tastini e crea le regioni associate
c
      size=1.0
      font=1
      rot=0.0
      ita=0
#if defined(_DOS_) 
      call set_text_attribute@(font,size,rot,ita)
#endif
      kact = 0
      do 100 i = nbox0 , nboxn
         widb=lbox(i)-2
         heib=20-2
         ix = xbox(i) -10+1
         iy = ybox(i) -30+1
         ix1 = ix + widb
         iy1 = iy + heib
         icol=ColTast
#if defined(_DOS_) 
         call fill_rectangle@(ix,iy,ix1,iy1,icol)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
         call fill_rectangle(ix,iy,ix1,iy1,icol)
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
         RES= SETCOLOR ( icol )
         RES= RECTANGLE ($GFILLINTERIOR,ix,iy,ix1,iy1)
#endif

         if ( i.gt.9 ) then
            call Tasto (ix,iy,widb,heib,kact,2)
         endif
cdjw*         icol=1 !dark blue
         icol=15 !bright white
         ix=ix-1
         iy=iy-1
         ix1=ix1+1
         iy1=iy1+1
#if defined(_DOS_) 
         call rectangle@(ix,iy,ix1,iy1,icol)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
         call rectangle(ix,iy,ix1,iy1,icol)
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
         RES= SETCOLOR ( icol )
         RES= RECTANGLE ($GBORDER,ix,iy,ix1,iy1)
#endif
         iiz = Lung(strbox(i))
         if (i.gt.9) then
            ix=ix+5
         end if
         iy2=iy1-17
#if defined(_DOS_) 
         call draw_text@(strbox(i)(1:iiz),ix,iy2,icol)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
         call draw_text(strbox(i)(1:iiz),ix,iy2,icol)
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
         RES= SETCOLOR ( icol )
         CALL MOVETO(ix,iy2,xy)
         CALL OUTGTEXT(strbox(i)(1:iiz))
#endif
  100 continue
      size=1.0
#if defined(_DOS_) 
      call set_text_attribute@(font,size,rot,ita)
#endif
c
      return
      end
c-----------------------------------------------------------------------
      subroutine Tasto (ix,iy,widb,heib,jact,ideep)
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
      USE DFLIB
      INTEGER*2 RES
      TYPE (xycoord) poly(6)
#endif
      character*14 strbox, strapp
      common /cmenu/ xbox(78),ybox(78),lbox(78),strbox(78),nbox,nboxt,
     +               nbox0,nboxn,ColTast,strapp(11)
      integer*2 ix,iy,icol1,icol2,widb,heib,ColTast
      integer*2 handle,err,ixv(100),iyv(100),iz

      call hide_mouse_cursor
      kact = jact
   10 continue
      if (kact.eq.1) then
         icol1 = ColTast + 8
         icol2 = 8
      else
         icol1 = 8
         icol2 = ColTast + 8
      endif
      iz=6
#if defined(_DOS_) || defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
      ixv(1) = ix
      iyv(1) = iy+heib
      ixv(2) = ix+ideep
      iyv(2) = iy+heib-ideep
      ixv(3) = ix+widb-ideep
      iyv(3) = iy+heib-ideep
      ixv(4) = ix+widb-ideep
      iyv(4) = iy+ideep
      ixv(5) = ix+widb
      iyv(5) = iy
      ixv(6) = ix+widb
      iyv(6) = iy+heib
#endif

#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
      poly(1)%xcoord = ix
      poly(1)%ycoord = iy+heib
      poly(2)%xcoord = ix+ideep
      poly(2)%ycoord = iy+heib-ideep
      poly(3)%xcoord = ix+widb-ideep
      poly(3)%ycoord = iy+heib-ideep
      poly(4)%xcoord = ix+widb-ideep
      poly(4)%ycoord = iy+ideep
      poly(5)%xcoord = ix+widb
      poly(5)%ycoord = iy
      poly(6)%xcoord = ix+widb
      poly(6)%ycoord = iy+heib
#endif


#if defined(_DOS_) 
      call create_polygon@(ixv,iyv,iz,handle,err)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
      call fill_polygon(ixv,iyv,iz, icol1)
#endif
#if defined(_DOS_) 
      call fill_polygon@(handle,icol1,err)
      call delete_polygon_definition@(handle,err)
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
      RES= SETCOLOR ( icol1 )
      res = POLYGON($GFILLINTERIOR, poly, iz)
#endif

#if defined(_DOS_) || defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
      ixv(3) = ix+ideep
      iyv(3) = iy+ideep
      ixv(6) = ix
      iyv(6) = iy
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
      poly(3)%xcoord = ix+ideep
      poly(3)%ycoord = iy+ideep
      poly(6)%xcoord = ix
      poly(6)%ycoord = iy
#endif

#if defined(_DOS_) 
      call create_polygon@(ixv,iyv,iz,handle,err)
#endif
#if defined(__LIN__) || defined (__GIL__) || defined (__MAC__) 
      call fill_polygon(ixv,iyv,iz,icol2)
#endif
#if defined(_DOS_) 
      call fill_polygon@(handle,icol2,err)
      call delete_polygon_definition@(handle,err)
#endif
#if defined(CRY_FORTINTEL) || defined (CRY_FORTDIGITAL) 
      RES= SETCOLOR ( icol2 )
      res = POLYGON($GFILLINTERIOR, poly, iz)
#endif
c
      if (kact.eq.0) goto 20
      kact = kact -1
#if defined(_DOS_) 
      call sleep@ (0.1)
#endif
      go to 10
20    call display_mouse_cursor
      return
      end
c-----------------------------------------------------------------------
      function kebox(ix,iy)
      character*14 strbox, strapp
      common /cmenu/ xbox(78),ybox(78),lbox(78),strbox(78),nbox,nboxt,
     +               nbox0,nboxn,ColTast,strapp(11)
      integer*2 ix,iy,ix1,iy1,widb,heib,ColTast
c
      kebox = -1
c     if (ix.ge.80) return
      heib = 20
      do 100 i = nbox0 , nboxn
         widb = lbox(i)
         ix1 = xbox(i) - 10
         ix2 = xbox(i) + widb - 10
         iy1 = ybox(i) - 30
         iy2 = ybox(i) + heib - 30
         if (ix.gt.ix1.and.ix.lt.ix2.and.
     *       iy.gt.iy1.and.iy.lt.iy2) then
                 kebox = i - 10
                 return
         endif
  100 continue
c
      return
      end
c-----------------------------------------------------------------------
      function kespec(ix,iy,NumSpec)
      character*14 strbox, strapp
      common /cmenu/ xbox(78),ybox(78),lbox(78),strbox(78),nbox,nboxt,
     +               nbox0,nboxn,ColTast,strapp(11)
      common /screen/ kscreen,jy,ivd,kscrx,kscry,jscreen,kprog,ifMouse
      integer*2 ix,iy,widb,heib,ColTast
c
      kespec = -1
      widb=25
      heib=20
      iyb = kscry - 25
      iy1b = iyb + heib
      if (iy.lt.iyb) return
      ixb = 300
      do 100 i = 1 , NumSpec
      ixb = ixb + widb + 10
      ix1b = ixb + widb
      if (ix.gt.ixb.and.ix.lt.ix1b.and.
     *    iy.gt.iyb.and.iy.lt.iy1b) then
                                     kespec = i
                                     return
                                    endif
  100 continue
c
      return
      end
