code for m1m3                                                                   
      subroutine m1m3 (udm,tdm)                                                 
      dimension udm(9), tdm(3,3)                                                
      do 100 i = 1,3                                                            
      do 200 j= 1,3                                                             
      k = (i-1)*3+j                                                             
      tdm(i,j) = udm(k)                                                         
  200 continue                                                                  
  100 continue                                                                  
      return                                                                    
      end                                                                       
c                                                                               
code for ospr                                                                   
      subroutine ospr( a,b,c)                                                   
c-----                                                                          
c----- orthogonal scalar product                                                
c-----                                                                          
      dimension a(3),b(3)                                                       
      c=0.0                                                                     
      idm = 3                                                                   
      do 10 i=1,idm                                                             
      c = c+a(i)*b(i)                                                           
   10 continue                                                                  
      return                                                                    
      end                                                                       
