c---------------------------------------------------------------------
c-- shelx output
      subroutine outshe
      character fmt*4,line*4,linl*80,xyz(3)
      common/unit/ln,lo,ifour,jrel,iscra,jhost,itle(20),jgrap
      common/trig/sint(450),pi,twopi,dtor,rtod
      common /atoms/ x(5,200),nz(200),idumx(21)
      common /const/ nex,np(3),numset,npic,nats,jall,nset,iset(20),factr
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *        kpol,jsys,lat,indv(32),mpv(64),jspini,dmins,ncawu,ncwu
      common /erl/ kmat(48,3,3),tmat(48,3),nt(16),lsys,ngen
     *,irot(48,48),kvet(10),jvet(8),nori,modul(3),nss(3)
      common/rc/p(6),cx(9),dumm(9)
      common/c/flgw(30),flgd(30),avr(30),dcv(50),slope,flgk,bt,sc,del,ks
      common/sf/al(4,8),bs(4,8),cl(8),nw(8),no(8),nk,nasu,f(9),nalf(8)
      common /emap/ jrecyc,krecy,erre,erreo,nemap(4),biso,bfac(200),lsx,
     *              oscale,errem,fomin,tsigma,maxcyc,jrest
      dimension x2(5),kpunt(8,2)
      dimension abc(3),ang(3),latsx(7)
      data xyz/'X','Y','Z'/
      data latsx / 1,5,6,7,2,4,3 /
c
c
      write(jhost,100) (itle(i),i=1,18)
  100 format('TITL    ',18a4)
      do 110 i=1,3
         abc(i)=cx(i)
         ang(i)=cx(i+3)
  110 continue
      ilat=latsx(lat)
      if (icent.eq.0) then
                        ilat=-ilat
                        nop2=noper
                      else
                        nop2=noper*2
                      endif
      wl=0.71069
      p005=0.005
      write(jhost,120) wl,abc,ang,nop2,(p005,i=1,6)
  120 format('CELL  ',f8.5,2x,6f9.3,/,
     *       'ZERR  ',i8,2x,6f9.3)
      write(jhost,130) ilat
  130 format('LATT ',2i2)
c
c procedura che scrive la parte relativa alla simmetria del file
c di input a shelx
      if (noper.gt.1) then
        do 170  k=2,noper
             linl(1:4)='SYMM' 
               do 140 i=5,80
  140             linl(i:i)=' '
               ip=6
               do 160 j=1,3
                 iplus=0
                 if (tmat(k,j).gt.0.001) then
                   write(linl(ip:),'(f8.5)') tmat(k,j)
                   ip=ip+8
                   iplus=1
                 endif
                 do 150 i=1,3
                   ii=kmat(k,j,i)
                   if (ii.ne.0) then
                     if (ii.lt.0) then
                         linl(ip:ip)='-'
                         ip=ip+1
                     else
                         if (iplus.eq.1) then
                           linl(ip:ip)='+'
                           ip=ip+1
                         endif
                     endif
                     write(linl(ip:ip),'(a1)') xyz(i)
                     ip=ip+1
                     iplus=1
                   endif
 150             continue
                 if (j.lt.3) then
                  write(linl(ip:ip),'(a1)') ','
                  ip=ip+1
                  iplus=0
                 endif
 160           continue
               write(jhost,'(a)') linl(1:79)
               ip=1
               iplus=0
 170  continue
      endif
      do 180 i=1,nk
      do 180 j=1,2
  180 kpunt(i,j)=0
      ish=0
      do 190 i=1,nk
      if (no(i).eq.6) then
                        ish=ish+1
                        kpunt(ish,1)=i
                        kpunt(i,2)=ish
                      endif
  190 continue
      do 200 i=1,nk
      if (no(i).eq.1) then
                        ish=ish+1
                        kpunt(ish,1)=i
                        kpunt(i,2)=ish
                      endif
  200 continue
      do 205 i=1,nk
      if (kpunt(i,1).eq.0) then
                             ish=ish+1
                             kpunt(ish,1)=i
                             kpunt(i,2)=ish
                           endif
  205 continue
      write(linl(1:38),210) (nalf(kpunt(i,1)),i=1,nk)
      call ucase(linl(1:38))
      write(jhost,'(a)') linl(1:38)
  210 format('SFAC  ',8(2x,a2))
      write(jhost,220) (nw(kpunt(i,1)),i=1,nk)
  220 format('UNIT  ',8i4)
      write(jhost,240)
  240 format('MERG      2',/,
     *       'OMIT      4',/,
     *       'L.S.      4',/,
     *       'LIST      1',/,
     *       'FMAP      2',/,
     *       'PLAN     25',/,
     *       'FVAR    1.0')
      denom=8.0*pi*pi
      do 260 i=1,nats
      izpki=iabs(nz(i))/100
      if (izpki.ne.0) then
                        izpki=kpunt(izpki,1)
                        izpk2=kpunt(izpki,2)
                        uiso=bfac(i)/denom
                        do 245 j=1,3
  245                   x2(j)=x(j,i)
                        write(line(1:2),270) nalf(izpk2)
                        line(3:4)='  '
                        x2(4)=x(5,i)+10.0
                        in=2
                        if (i.gt.99) in=3
                        write(fmt,280) in
                        write(line(5-in:4),fmt) i
                        do 250 j=1,4
                        if (line(j:j).eq.' ') line(j:j)='0'
  250                   continue
                        call ucase(line)
                        write(jhost,290) line,izpki,(x2(j),j=1,4),uiso
                      endif
  260 continue
  270 format(a2)
  280 format(2h(i,i1,1h))
  290 format(a4,i5,5f10.5)
      write(jhost,300)
  300 format('           ',/,
     *       'HKLF      4',/,
     *       'END        ')
      uiso=bt/denom
      do 360 i=1,nats
      izpki=iabs(nz(i))/100
      if (izpki.eq.0) then
                        izpki=1
                        do 345 j=1,3
  345                   x2(j)=x(j,i)
                        line(1:2)='Q '
                        line(3:4)='  '
                        x2(4)=x(5,i)+10.0
                        in=2
                        if (i.gt.99) in=3
                        write(fmt,280) in
                        write(line(5-in:4),fmt) i
                        do 350 j=1,4
                        if (line(j:j).eq.' ') line(j:j)='0'
  350                   continue
                        write(jhost,290) line,izpki,(x2(j),j=1,4),uiso
                      endif
  360 continue
      return
      end
