c------------------------------------------------------------------
      subroutine xcmprp(string,lstri)
c
      character*(*) string
      character ib,string1*120
c
      ib=' '
      string1=ib
      k=len(string)
      j=1
      do 10 i=1,k
      if (string(i:i).ne.ib) then
                               string1(j:j)=string(i:i)
                               j=j+1
                             endif
   10 continue
      lstri=j-1
      string(1:lstri)=string1(1:lstri)
      if (j.le.k) then
                    do 20 i=j,k
   20               string(i:i)=ib
                  endif
      return
      end
