code for xflori
c-----------------------------------------------------------------------
      subroutine xflori
c
c-- floating origin subroutine
c
c-- kpol code is computed to take
c-- into account floating origin.
c-- if kpol.eq.0 then no floating origin
c-- if kpol.ne.0 then the floating direction
c--                   corresponds to 1's in the code
c                     e.g.   kpol=001 float. origin along z
c                            kpol=100 float. origin along x
c
      dimension jfl(3)
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
      if (icent.eq.1) then
                        kpol=0
                      else
                        do 60 k=1,3
                          jfl(k)=0
                          do 50 i=1,noper
                            do 40 j=1,3
                              jfl(k)=jfl(k)+soper(j,k,i)
   40                       continue
   50                     continue
                          if (jfl(k).ne.noper) then
c--  non - floating direction
                                                 jfl(k)=0
                                               else
c--  floating direction
                                                 jfl(k)=1
                                               endif
   60                   continue
                        mult=100
                        kpol=0
                        do 70 i=1,3
                        kpol=kpol+jfl(i)*mult
                        mult=mult/10
   70                   continue
                      endif
      return
      end
code for kspecb
c----------------------------------------------------------------------
      function kspecb(newvet,xo,xn,key,itype,iser,khead,iactn)
c
c-- function to handle atoms in special position
c
c   return value
c          kspecb = +1 atom in general position
c                 = -1 atom in special position
c-- meaning of parameters
c     xo    = array containing original values for x,y,z,occ,u's,c-occ
c     xn    = array containing modified values for x,y,z,occ,u's,c-occ
c    key    = array containing conditions for      x,y,z,    u's,c-occ
c  itype    = type of atom
c   iser    = serial number
c  khead    =  0  heading for special position to be print
c           =  1  heading not to be printed
c     iactn =     action to be taken :
c           =  1  compute crystallographic occupation
c           =  2  generate key array and update x's and u's
c           =  3  print information
c    newvet = matrix containing information about 
c             the symmetry operators involved
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
      dimension xo(11),xn(11),key(10),newvet(48,3)
c
      do 10 i=1,9
   10 key(i)=i
      key(10)=1
      call xnuova(xo,xn,key,n,newvet,iser)
      kspecb=1
      if (key(10).gt.1) then
         kspecb=-1
         if (iactn.gt.1) then
            if (key(10).ne.1) then
                                call xelles(newvet,n,key)
                                call xtherm(newvet,n,key)
                              endif
             if (iactn.gt.2) call xprint(key,itype,iser,xn,khead)
                         endif
                        endif
c
      return
      end
code for xnuova
c------------------------------------------------------------------
      subroutine xnuova(xo,xn,key,nr,newvet,iser)
c--
c-- subroutines to recognize if a peak is in a special position.
c-- if it is very close to it, the peak is "moved" in it.
c--
c-- check if atoms are in special position
c-- computing the distance between an atom
c-- and its symmetry equivalents
c--
      dimension xo(11),xn(11),key(10),vet(2)
      dimension xeq(3),m(3,3),s(3),xb1(3),xb2(3),newvet(48,3)
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
c
      vet(1)= 1.0
      vet(2)=-1.0
      do 20 i=1,11
   20 xn(i)=xo(i)
      do 30 i=1,3
      xb1(i)=xo(i)
   30 xb2(i)=xo(i)
      ifin=icent+1
      nr=0
      do 50 kt=1,ncentr
        do 50 ifi=1,ifin
          coef=vet(ifi)
          do 50 k=1,noper
            if (k.eq.1.and.ifi.eq.1.and.kt.eq.1) go to 50
            call xequi(xb2,k,coef,xeq,kt)
            call xdist(xb2,xeq,d,s)
            if (d.le.ddmin) then
                              nr=nr+1
                              newvet(nr,3)=k*coef
                              do 40 j=1,3
                                xb1(j)=xb1(j)+xb2(j)+s(j)
   40                         continue
                            endif
   50 continue
      if (nr.ne.0) then
                do 70 l=1,nr
                   k=newvet(l,3)
                   coef=1.0
                   if (k.lt.0) then
                                 k=-k
                                 coef=-1.0
                               endif
                   do 60 ii=1,3
                      do 60 j=1,3
                         m(ii,j)=coef*soper(ii,j,k)
   60              continue
                   call xdepac(m,ind,inew)
                   newvet(l,1)=inew
                   newvet(l,2)=ind
   70           continue
                do 80 j=1,3
                   xb1(j)=xb1(j)/float(nr+1)
   80           continue
                else
                do 90 j=1,3
                   newvet(1,j)=1
   90           continue
                endif
c
      kcond=nr+1
      do 100 i=1,3
  100 xn(i)=xb1(i)
      xn(11)=1.0/float(kcond)
      key(10)=kcond
      return
      end
code for xequi
c--------------------------------------------------------------------
      subroutine xequi(xb2,k,coef,xeq,kt)
c--
c-- computes the symmetry equivalent of an atom
c--
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
      dimension xb2(3),xeq(3)
c
      do 10 l=1,3
      ktl=(kt-1)*3+l
      xeq(l)=soper(l,4,k)+centrt(lat,ktl)
      do 10 j=1,3
   10 xeq(l)=xeq(l)+xb2(j)*coef*soper(l,j,k)
      return
      end
code for xdist
c----------------------------------------------------------------------
      subroutine xdist(xb2,xeq,d,s)
c--
c-- subroutine used to compute the distance between
c-- an atom and its equivalent
c--
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
      dimension xeq(3),xb2(3),delta(8,3),dp(3),ds(3),s(3)
c
      do 20 i=1,3
         d=xeq(i)-xb2(i)
         dp(i)=d-int(d)
         if (dp(i).le.0.0) then
                           ds(i)=dp(i)+1.0
                           else
                           ds(i)=dp(i)-1.0
                           endif
   20 continue
      delta(1,1)=dp(1)
      delta(1,2)=dp(2)
      delta(1,3)=dp(3)
      delta(2,1)=dp(1)
      delta(2,2)=dp(2)
      delta(2,3)=ds(3)
      delta(3,1)=dp(1)
      delta(3,2)=ds(2)
      delta(3,3)=dp(3)
      delta(4,1)=dp(1)
      delta(4,2)=ds(2)
      delta(4,3)=ds(3)
      delta(5,1)=ds(1)
      delta(5,2)=dp(2)
      delta(5,3)=dp(3)
      delta(6,1)=ds(1)
      delta(6,2)=dp(2)
      delta(6,3)=ds(3)
      delta(7,1)=ds(1)
      delta(7,2)=ds(2)
      delta(7,3)=dp(3)
      delta(8,1)=ds(1)
      delta(8,2)=ds(2)
      delta(8,3)=ds(3)
      dimi=99999.9
      do 40 i=1,8
         d2=delta(i,1)*delta(i,1)*aaa(1)
     &     +delta(i,2)*delta(i,2)*aaa(2)
     &     +delta(i,3)*delta(i,3)*aaa(3)
     &     +delta(i,1)*delta(i,2)*aaa(4)
     &     +delta(i,1)*delta(i,3)*aaa(5)
     &     +delta(i,2)*delta(i,3)*aaa(6)
         d=sqrt(d2)
         if (d.lt.dimi) then
                        dimi=d
                        do 30 j=1,3
                           s(j)=delta(i,j)
   30                   continue
                        endif
   40 continue
      d=dimi
      return
      end
code for xdepac
c-----------------------------------------------------------------------
      subroutine xdepac(m,ind,inew)
c--
c-- using a code associated to the matrix in use
c-- finds out which kind of symmetry operator
c-- is involved
c--
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
      dimension m(3,3)
c
      call xcmat(m,mp)
c
      in=0
      do 10 i=1,64
         if (mp.eq.mpv(i)) in=i
   10 continue
c
      if (in.eq.0) then
                      write(ncwu,*) 'stop speclib - xdepac '
                      write(ncwu,'(3i4)') ((m(i,j),j=1,3),i=1,3)
                      write(ncwu,'(i10)')      mp
                      stop 'speclib - xdepac '
                    endif
c
      ind=in
      inw=1
      if (in.gt.32) then
                      inw=-1
                      in=in-32
                     endif
      inew=inw*indv(in)
      return
      end
code for xcmat
c------------------------------------------------------------------
      subroutine xcmat(m,mp)
c--
c-- this subroutine is used to transform a rotational
c-- matrix in a unique number to use like a pointer
c-- to recognize which kind of operator is involved
c-- ( mirror , 2-fold axis etc. )
c--
      dimension m(3,3)
c
      mp=0
      iexp=-1
      do 10 i=1,3
         do 10 j=1,3
            iexp=iexp+1
            if (m(i,j).ge.0) then
                               mp=mp+m(i,j)*2**iexp
                             else
                               mp=mp+2**iexp+2**(iexp+9)
                             endif
   10 continue
      return
      end
code for xwrifl
c----------------------------------------------------------------------
      subroutine xwrifl
c
c-- write information about freely floating origin
c
      character*3 xyz,xyzfr
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
c
      kdummy=kpol
      xyz='xyz'
      do 10 i=1,3
      j=4-i
      ipr=mod(kdummy,10)
      kdummy=kdummy/10
      xyzfr(j:j)=' '
      if (ipr.eq.1) xyzfr(j:j)=xyz(j:j)
   10 continue
      if (kpol.ne.0) write(ncwu,30) xyzfr
   30 format(/' *** warning *** freely floating origin along ',a3,/)
      return
      end
code for xelles
c------------------------------------------------------------------
      subroutine xelles(newvet,n,key)
c
c-- this subroutine computes the
c-- conditions for least squares shifts
c
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
 
      integer xeq(3),xyz(3),xyo(3),iflc(3),key(10)
      integer mult(3),newvet(48,3)
c
      nn=n+1
      mult(1)=-1
      mult(2)= 1
      mult(3)= 2
      xyo(1)=3
      xyo(2)=11
      xyo(3)=13
      do 10 i=1,3
         xyz(i)=xyo(i)
   10 continue
      do 30 m=1,n
         coef=1.0
         k=newvet(m,3)
         if (k.lt.0) then
                       k=-k
                       coef=-1
                     endif
         do 20 l=1,3
            xeq(l)=0
            do 20 j=1,3
   20          xeq(l)=xeq(l)+xyo(j)*soper(l,j,k)*coef
         do 25 j=1,3
            if (xyz(j).ne.0) then
                               xyz(j)=xyz(j)+xeq(j)
                             endif
   25 continue
   30 continue
      ifl=0
      do 35 i=1,3
      if (xyz(i).ne.0) then
                         if (mod(xyz(i),nn).ne.0) ifl=1
                       endif
   35 continue
      if (ifl.eq.0) then
                      do 38 i=1,3
   38                 xyz(i)=xyz(i)/nn
                    endif
c
      do 40 i=1,3
         iflc(i)=0
   40 continue
      k=0
      do 60 i=1,3
      if (xyz(i).ne.0) then
      if (k.eq.0) then
                    k=1
                    if (xyz(i).lt.0) then
                                       do 50 j=1,3
                                          xyz(j)=-xyz(j)
   50                                  continue
                                     endif
                  endif
                  endif
   60 continue
      do 110 i=1,2
         if (iflc(i).eq.0) then
            if (xyz(i).ne.0) then
               k=1
               ifl2=0
               do 80 j=i+1,3
                  ifl1=0
                  do 70 l=1,3
                     if (xyz(i)*mult(l).eq.xyz(j)) then
                                                     iflc(j)=mult(l)
                                                     iflc(i)=1
                                                     ifl1=1
                                                   endif
                     if (ifl1.eq.0) then
                     if (xyz(i).eq.xyz(j)*mult(l)) then
                                                     iflc(i)=mult(l)
                                                     iflc(j)=1
                                                     ifl1=1
                                                   endif
                                    endif
   70             continue
               ifl2=ifl2+ifl1
   80          continue
                  if (ifl2.ne.0) then
                                  l=0
                                  do 90 j=1,3
                                     if (iflc(j).ne.0) then
                                                         if (l.eq.0) l=j
                                                       endif
   90                             continue
                                  do 100 j=1,3
                           if (iflc(j).ne.0) xyz(j)=xyo(l)*iflc(j)
  100                             continue
                                 endif
                             endif
                       endif
  110 continue
      do 130 i=1,3
         ifl=0
         if (xyz(i).ne.0) then
                          do 120 j=1,3
                             do 120 k=1,3
                                if (xyz(i).eq.xyo(j)*mult(k)) ifl=1
  120                     continue
                          if (ifl.eq.0) xyz(i)=xyo(i)
                          endif
  130 continue
      do 150 j=1,3
         if (xyz(j).ne.0) then
            do 140 k=1,3
                    if (xyz(j).eq.xyo(k))   then
                                              xyz(j)=k
               else if (xyz(j).eq.2*xyo(k)) then
                                              xyz(j)=20+k
               else if (xyz(j).eq.-xyo(k))  then
                                              xyz(j)=-k
                                             endif
  140       continue
         endif
  150 continue
      do 155 i=1,3
        ix=xyz(i)
        ifl=0
        if (ix.lt.0) then
                       do 153 j=1,3
                          if (xyz(j).eq.-ix) ifl=1
  153                  continue
                       if (ifl.eq.0) xyz(i)=-xyz(i)
                     endif
  155 continue
      do 160 i=1,3
  160 key(i)=xyz(i)
      if (key(3).eq.23.or.key(3).eq.-3) key(3)=3
      return
      end
code for xprint
c----------------------------------------------------------------------
      subroutine xprint(key,itype,iser,xn,khead)
c
c-- print symmetry restrictions on atomic parametrs
c
c--   key  array containing symmetry restrictions
c   itype  atom type (hollerith*4)
c    iser  serial
c      xn  array containing values for atomic parameters
c
      character line*42,keyc(9)*2
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
      character cbuff*124
      common /atpos/ cbuff
      dimension key(10),xn(11)
      data keyc/' x',' y',' z','11','22','33','23','13','12'/
c
      k=1
      mm=5
      do 80 j=1,9
      if (j.le.4) then
                    mm=4
                  else
                    mm=5
                  endif
       kj=key(j)
       l=k+5
       if (kj.ge.0) then
            if (kj.eq.0) then
                           write(line(k:l),'(5h  0  )')
       else if (kj.le.j) then
                           write(line(k:l),'(1h ,a2,3h   )') keyc(kj)
       else if (kj.gt.9) then
            if (kj.gt.20) then
                          kj=kj-20
                          write(line(k:l),'(a2,3h*2 )') keyc(kj)
                          else
                          kj=kj-10
                          write(line(k:l),'(a2,3h/2 )') keyc(kj)
                          endif
       else if (kj.lt.j) then
                           write(line(k:l),'(1h ,a2,2h  )') keyc(kj)
                         endif
                    else
                      kj=-kj
                      if (j.le.3) then
                          write(line(k:l),'(2h -,a1,2h  )')keyc(kj)(2:2)
                                  else
                          write(line(k:l),'(1h-,a2,2h  )') keyc(kj)
                                  endif
                    endif
       k=k+mm
   80 continue
c
      if (jspini.eq.1) then
c
c-- crystals output 
c
         if (khead.eq.0) then
                           write(ncwu,100)
                           khead=1
  100 format(
     *' type serial    symmetry restrictions on atomic parameters')
                         endif
         write(ncwu,200) itype,iser,line
  200 format(2x,a4,i4,4x,a42)
                       else
c
c-- sir output
c
         write(cbuff(47:124),300) (xn(i),i=1,3),xn(11),line
c 300 format(1h+,45x,3f7.3,f8.4,1x,a42)
  300 format(3f7.3,f8.4,1x,a42)
                       endif
      return
      end
code for xtherm
c-----------------------------------------------------------------------
      subroutine xtherm(newvet,nr,key)
c
c-- this subroutine computes the conditions for
c-- thermal parameters according to:
c
c           w.j.a.m. peterse and j.h. palme
c           acta cryst. (1966). 20, 147
c
      common/spec/ soper(3,4,24),centrt(7,12),aaa(6),noper,ncentr,icent,
     *         kpol,jsys,lat,indv(32),mpv(64),jspini,ddmin,ncawu,ncwu
 
      integer newvet(48,3),ivet(48),s,q(3,3),binv(9,6),ijt(6,2),itt(6)
      integer q12,q13,q23,vterm(6),bin1(6,6),key(10)
c
      do 10 i=1,6
         vterm(i)=99
   10 continue
      itt(1)=1
      itt(2)=5
      itt(3)=9
      itt(4)=6
      itt(5)=3
      itt(6)=2
      do 18 k=1,6
      ict=0
      do 15 i=1,3
      do 15 j=1,3
      ict=ict+1
      if (ict.eq.itt(k)) then
                           ijt(k,1)=i
                           ijt(k,2)=j
                         endif
   15 continue
   18 continue
      ivet(1)=1
      do 20 i=1,nr
         ivet(i+1)=newvet(i,3)
   20 continue
      n=nr+1
      kk=0
      do 50 i=1,3
         do 50 j=1,3
            do 30 k=1,3
               do 30 l=1,3
                  q(k,l)=0
                  do 30 js=1,n
                     coef=1.0
                     s=ivet(js)
                     if (s.lt.0) then
                                   s=-s
                                   coef=-1
                                 endif
                     q(k,l)=q(k,l)+soper(i,k,s)*soper(j,l,s)
   30             continue
         q12=q(1,2)+q(2,1)
         q13=q(1,3)+q(3,1)
         q23=q(2,3)+q(3,2)
         kk=kk+1
         do 40 k=1,3
            binv(kk,k)=q(k,k)
   40    continue
         binv(kk,4)=q12
         binv(kk,5)=q13
         binv(kk,6)=q23
   50 continue
      do 60 k=1,6
         isk=0
         i=ijt(k,1)
         j=ijt(k,2)
         kk=itt(k)
         do 60 l=1,6
            bin1(k,l)=binv(kk,l)
   60 continue
      if (jsys.eq.5) then
                       do 70 i=4,6
                          do 70 l=1,6
                             bin1(i,l)=2*bin1(i,l)
   70                  continue
                     endif
      do 90 i=1,6
         ki=ijt(i,1)
         kj=ijt(i,2)
         ifl=0
         do 80 j=1,6
            if (bin1(i,j).ne.0) ifl=1
   80    continue
         if (ifl.eq.0) vterm(i)=0
   90 continue
      kk=1
      do 120 i=1,5
         ich=0
         if (vterm(i).eq.99) then
                               do 110 k=i+1,6
                                     do 110 is=-1,1,2
                                      do 110 js=0,1
                                        jsc=mod(js+1,2)
                                        do 110 ls=0,1
                                        lsc=mod(ls+1,2)
                                        mi=ls*2**js+lsc
                                        mk=ls*2**jsc+lsc
                                        ifls=0
                                        do 100 j=1,6
                                           ibi=bin1(i,j)*mi
                                           ibk=bin1(k,j)*is*mk
                                           if (ibi.ne.ibk) ifls=1
  100                                   continue
                                        if (ifls.eq.0) then
                                            if (ich.eq.0) kk=kk+1
                                            ich=1
                                            vterm(i)=kk*mk
                                            vterm(k)=kk*is*mi
                                            endif
  110                          continue
                               if (vterm(i).eq.99) vterm(i)=1
                             endif
  120 continue
      if (vterm(6).eq.99) vterm(6)=1
      jpreva=0
      jprevb=0
      do 150 i=1,6
         vterm(i)=6-vterm(i)
               if (vterm(i).eq.0) then
                                    if (jprevb.eq.0) then
                                                 vterm(i)=20+i
                                                 jprevb=i
                                                else
                                                  vterm(i)=20+jprevb
                                                endif
          else if (vterm(i).eq.3) then
                                    if (jprevb.eq.0) then
                                                  vterm(i)=i
                                                  jprevb=i
                                                else
                                                  vterm(i)=jprevb
                                                endif
          else if (vterm(i).eq.4) then
                                    if (jpreva.eq.0) then
                                                  vterm(i)=i
                                                  jpreva=i
                                                else
                                                  vterm(i)=jpreva
                                                endif
          else if (vterm(i).eq.5) then
                                    vterm(i)=i
          else if (vterm(i).eq.6) then
                                    vterm(i)=0
          else if (vterm(i).eq.9) then
                                    if (jprevb.eq.0) then
                                                  vterm(i)=-i
                                                  jprevb=i
                                                else
                                                  vterm(i)=-jprevb
                                                endif
                                  endif
  150 continue
      do 170 j=4,9
      i=j-3
      jv=vterm(i)
      if (jv.ne.0) then
         if (jv.le.9) then
                        jv=jv+3*isign(1,jv)
                      else
                        jv=(mod(jv,20)+3)+20
                      endif
                   endif
  170 key(j)=jv
      if (key(9).le.6.and.key(9).ge.4) key(9)=key(9)+10
      return
      end
