                                           endif
                           call site(newvet,keyx,jump,k)
                           do 945 k=1,3
  945                      x(k,i)=xn(k)
                         endif
      if (iprin.gt.1) then
          if (ifspec.eq.0) then
                             write(lo,'(a)') cbuff(1:50)
                           else
                             write(lo,'(a)') cbuff
                           endif
                      endif
  950 continue
      return
      end
c-----------------------------------------------------------------------
      subroutine site(newvet,key,jump,k)
c
      character*80 buff
      character*124 cbuff
c
      dimension newvet(48,3),key(10),lsqv(28),lterm(2,30)
      character         string*6
      character lsqt(28)*6,itrt(30)*12,sitin(85)*6
