      ifnq=1
      nreft=0
      emin=0.0
      ntr10=0
      numk=0
      ntrt=0
      ntrs=0
      nrefp=0
      emax=0.4
corig ecros=0.75
      ecros=1.00
      ecr1=3.0
corig iecros=75
      iecros=100
      iecr1=300
