      subroutine nctet1(aa,co,cc)
c--test for the correct coincidence (non-centro)
c
      common /nctst1/ ns,br(19),nb(19),fnb(19)
c
      bb=abs(aa)
      do 1060 i=1,ns
      if(br(i).le.bb)  then
                         nb(i)=nb(i)+1
                         a=cc*co
                         if(a) 1060,1050,1050
1050                     continue
                         fnb(i)=fnb(i)+1.0
                       endif
1060  continue
      return
      end
