      if(istore(lx)-ibuf(1)) 9100,2320,9100
2320  continue
      if(kredf(temp(ja),3)) 9100,2330,9100
2330  continue
      store(lz+1)=temp(ja)
      store(lz+11)=temp(ja+1)
      store(lz+16)=temp(ja+2)
      lz=lz+md3
      return
9000  continue
      jst03=1
      return
9100  continue
      jst03=-1
      return
