code for nfrm16
      subroutine nfrm16(nset)
c--form an empty list 16, set(s) of phases
c
      common /param/ numb,dum(10)
      common/lst016/l16,ld16p,m16,m16d,md16d,n16,n16p,l16p
     1 ,l16a,l16k,n16k,dumm
      common/xlimi/nfl,lfl,nl,idummy(3),nfls
      common /data/ store(5000)
      dimension istore(5000)
      equivalence(store,istore)
c
      nfl=nfls
      in=16
      call xcln(in)
      l16p=nfl
      md16d=11
      m16d=1
      n16r=15
      n16p=n16r+nset*2
      lj=nfl+n16p-1
      do 1000 i=nfl+5,lj
1000  store(i)=0.0
      store(nfl+5)= md16d
      store(nfl+6)= m16d
      store(nfl+7)= numb
      store(nfl+8)= n16r
      store(nfl+9)= n16p
      store(nfl+10)=nset
      m=n16r+nfl
      do 1010 i=1,nset
      store(m)=0.0
1010  m=m+1
      do 1020 i=1,nset
      istore(m)=0
1020  m=m+1
      n16k=numb*m16d+md16d
      ln=n16p+n16k*nset
      call sowl(ln,in,lsn,ld16p,lk,lm,lj)
      store(nfl+2)=lsn
      call xup(ld16p,istore(l16p),n16p)
      llw=ld16p+ln-1
      call swlin(16,lsn,ld16p,llw,n16p,ln)
      l16=ld16p+n16p
      l16a=nfl+n16p
      l16k=l16a+md16d
      l16b=l16k+numb
      l16c=l16b+numb
      do 3000 k=1,nset
      m16k=l16k
      m16b=l16b
      m16c=l16c
      do 2000 i=1,numb
      istore(m16k  )=0
      m16k=m16k+1
      m16b=m16b+1
      m16c=m16c+1
2000  continue
      call xup(l16,istore(l16a),n16k)
      l16=l16+n16k
3000  continue
      call xdump
      return
      end
