      SUBROUTINE autov(a,eigen,t,icod)
      dimension t(3,3),a(3,3),aik(3),eigen(3)
c
      itmax=50
      n=3
      nm1=n-1
      eps1=.1e-10
      eps2=eps1
      eps3=.1e-7
      sigma1=0
      offdsq=0
      do 2 i=1,3
      do 2 j=1,3
    2 t(i,j)=0.
      do 5 i=1,n
      sigma1=sigma1+a(i,i)**2
      t(i,i)=1.0
      ip1=i+1
      if (i.ge.n) go to 6
                    do 5 j=ip1,n
   5                offdsq=offdsq+a(i,j)**2
   6  s=2.*offdsq+sigma1
c
c  inizio iter. jacobi
c
      do 26 iter=1,itmax
      do 20 i=1,nm1
      ip1=i+1
      do 20 j=ip1,n
      q=abs(a(i,i)-a(j,j))
      if(q.le.eps1) goto 9
      if(abs(a(i,j)).le.eps2) goto 20
      p=2.*a(i,j)*q/(a(i,i)-a(j,j))
      spq=sqrt(p*p+q*q)
      csa=sqrt((1.+q/spq)/2.)
      sna=p/(2.*csa*spq)
      goto 10
    9 csa=1./sqrt(2.d0)
      sna=csa
   10 continue
c
      do 11 k=1,n
      holdki=t(k,i)
      t(k,i)=holdki*csa+t(k,j)*sna
   11 t(k,j)=holdki*sna-t(k,j)*csa
c
      do 16 k=i,n
      if(k.gt.j) goto 15
      aik(k)=a(i,k)
      a(i,k)=csa*aik(k)+sna*a(k,j)
      if(k.ne.j) goto 14
      a(j,k)=sna*aik(k)-csa*a(j,k)
   14 goto 16
   15 holdik=a(i,k)
      a(i,k)=csa*holdik+sna*a(j,k)
      a(j,k)=sna*holdik-csa*a(j,k)
   16 continue
c
      aik(j)=sna*aik(i)-csa*aik(j)
c
      do 19 k=1,j
      if (k.le.i) then
                    holdki=a(k,i)
                    a(k,i)=csa*holdki+sna*a(k,j)
                    a(k,j)=sna*holdki-csa*a(k,j)
                  else
                    a(k,j)=sna*aik(k)-csa*a(k,j)
                  endif
   19 continue
   20 a(i,j)=0.0
c
      sigma2=0.0
      do 21 i=1,n
      eigen(i)=a(i,i)
   21 sigma2=sigma2+eigen(i)**2
      if (abs(sigma2).gt.0.00001) then
         if(1.0-sigma1/sigma2.ge.eps3) goto 25
      end if
      icod=1
      return
   25 continue
   26 sigma1=sigma2
      icod=0
c
      return
      end
c ------------------------------------------------------------------
      SUBROUTINE molt1(a,b,c)
      dimension a(3, 3), b(3), c(3)
c
      do 20 i=1,3
         c(i)=0
         do 10 j=1,3
            c(i)=c(i)+a(i,j)*b(j)
   10    continue
   20 continue
c
      return
      end
