     3     , Lim1,Lim2,Lim3,Lim4,PI,Xmin,Ymin,LegCol,nlp, nMaxLeg
     5     , NumLeg, iPlotType , RK(4), VV(10,3), LE(800,2), RI(3)
      DIMENSION B(3, 3), BOpt(3, 3)
c
      DO 20 j = 1 , 3
         DO 10 i = 1 , 3
            B(i, j) = 0
   10    CONTINUE
         B(j, j) = 1
   20 CONTINUE
c
      CALL CombRot(B)
*     CALL RotAtoms(B, 2)
      CALL RotAtoms(B, 1)
      CALL SetScale(4)
      SupMin = abs((RMAX1-Rmin1)/RS(1))
      DO 40 i = 1 , 3
         DO 30 j = 1 , 3
            BOpt(i, j) = 0
            B   (i, j) = 0
*           BOpt(i, j) = B(i, j)
   30    CONTINUE
         BOpt(i, i) = 1
         B   (i, i) = 1
   40 CONTINUE
      RK(1) = 0
      DO 90 NuR = 1 , 18
         RK(1) = RK(1) + 10
         RK(2) = 3
         DO 44 j = 1 , 3
            DO 42 i = 1 , 3
               B(i, j) = 0
   42       CONTINUE
            B(j, j) = 1
   44    CONTINUE
         CALL CombRot(B)
         Rmin1 = 99999.
         Rmin2 = 99999.
         RMAX1 = -99999.
         RMAX2 = -99999.
         CALL RotAtoms(B, 2)
         CALL SetScale(4)
         sca=abs((RMAX1-Rmin1)/RS(1))
         IF (sca .lt. SupMin) then
                                SupMin = sca
                                DO 80 i = 1 , 3
                                   DO 70 j = 1 , 3
                                      BOpt(i, j) = B(i, j)
   70                              CONTINUE
   80                           CONTINUE
                              END IF
   90 CONTINUE
      Rmin1 = 99999.
      Rmin2 = 99999.
      RMAX1 = -99999.
      RMAX2 = -99999.
      CALL RotAtoms(BOpt, 1)
c
      RETURN
      END
