      SUBROUTINE Balls
      IMPLICIT REAL*4 (M)
      CHARACTER *1 Lett
      CHARACTER *5 Lab
      CHARACTER *80 Titolo
      INTEGER *4 CC,O1,O2,O3,O4,X11,Y11,R
c
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      COMMON /Crt/ iBarre(62, 4),Lett(72),NumBar(70), iCheBarre(70, 6)
     2     , Rmin1, Rmin2, RMAX1, RMAX2, XMax, YMax, DDX, DDY, NPX, NPY
     3     , Lim1,Lim2,Lim3,Lim4,PI,Xmin,Ymin,LegCol,nlp, nMaxLeg
     5     , NumLeg, iPlotType , RK(4), VV(10,3), LE(800,2), RI(3)
      COMMON /Contr/ NF,iFirstTime,Titolo,Ratio,IfCont,IfEdge,IfScale,
     *               kplty,radii(8), IfScaleB
      DIMENSION S(3)
c
      DO 20 i = 1 , (NAT + nv) - 1
         DO 10 K = i + 1 , NAT + nv
            O1 = iOrd(i)
            O2 = iOrd(K)
            O3 = xyz(3,O1)
            O4 = xyz(3,O2)
            IF (O3.GT.O4) THEN
               iOrd(i) = O2
               iOrd(K) = O1
            END IF
   10    CONTINUE
   20 CONTINUE
c
      CALL NewPage
c
      DDX = (XMax - Xmin) / NPX
      DDY = (YMax - Ymin) / NPY
      DO 50 III = 1 , NAT + nv
         IJ = iOrd(III)
         if (RS(IJ).LE.0.0) go to 50
c
c        if (IfEdge.eq.1 .OR. IJ.le.NAT) then
c           CALL DisePalle(IJ, X11, Y11, R, CC)
c        end if
c
      X11 = ipix(1,IJ)
      Y11 = ipix(2,IJ)
      RR(IJ) = RS(IJ)
      CC = ICol(IJ)
      R = ipix(3,IJ)
c      R = R+2
         DO 40 NLL = 1 , NumLeg
            N1 = LE(NLL, 1)
            if (IfEdge.eq.0 .AND. N1.gt.NAT) GOTO 40
            N2 = LE(NLL, 2)
            IF (N1.NE.IJ.AND.N2.NE.IJ) GOTO 40
            IF (N1 .LT. 0)  GOTO 40
            IF (RR(N1) .LE. 0 .OR. RR(N2) .LE. 0)  GOTO 40
 
            RAN1 = xyz(3,N1)
            RAN2 = xyz(3,N2)
            IF (RAN1.GT.RAN2) THEN
               NDUM = N1
               N1 = N2
               N2 = NDUM
            END IF
            DO 30 i = 1 , 3
               DU1 = xyz(i,N1)
               DU2 = xyz(i,N2)
               S(i) = DU1 - DU2
   30       CONTINUE
            SS = S(1) ** 2 + S(2) ** 2
            if (SS .LT. 0.001) goto 34
            SSS = SS + S(3) ** 2
            RL = SQRT(SSS)
            RL2 = SQRT(SS)
            RN21 = xyz(1,N2)
            RN22 = xyz(2,N2)
            RN11 = xyz(1,N1)
            RN12 = xyz(2,N1)
            S1 = S(1)
            S2 = S(2)
            RR1 = RR(N1)
            if ( N2 .gt. NAT ) then
                                 RR2 = RR(N2)
                               else
                                 RR2 = RR(N2) - 2 * DDX
                               endif
            p1 = S1 / RL2 * RR2 + RN21
            p2 = S2 / RL2 * RR2 + RN22
            p3 = -S1 / RL * RR1 + RN11
            p4 = -S2 / RL * RR1 + RN12
            P3RN = (p3 - RN21) ** 2
            P4RN = (p4 - RN22) ** 2
            P3P4 = P3RN + P4RN
            P3P4 = SQRT(P3P4)
            IF (P3P4 .LT. RR(N2) .OR. P3P4 .LT. RR(N1))  GOTO 34
            RN13 = xyz(3,N1)
            RN23 = xyz(3,N2)
            DEL = ABS(RN13 - RN23)
            DIS = RL
            RAP = (DIS - DEL) / DIS
            if (IJ.gt.NAT) then
               CALL Linea0(p1, p2, p3, p4, 7)
            else
               CALL DisLegN(p1, p2, p3, p4, RAP)
               Ize = 0
               if (iPlotType .NE. 6) then
                  CALL Cerchio ( X11, Y11, R, CC, Ize, Ize)
               end if
            end if
c
   34       CONTINUE
            LE(NLL, 1) = -LE(NLL, 1)
   40    continue
         if (IJ.le.NAT) then
            CALL DisePalle(IJ, X11, Y11, R, CC)
            if (Lab(IJ)(1:1).eq.'Q' .and. iPlotType.ne.3) then
               IRT = 0.6 * R - 2
               if (IRT .gt. R-2) IRT = R-2
               if (IRT .lt. 1) IRT = 1
c              CCV = CC
c              CC = 0
c              CALL DisePalle(IJ, X11, Y11, IRT, CC)
c              CC = CCV
               IfF = -1
               iCC=0
               Call Cerchio( X11, Y11, IRT, iCC, IfF, iCC)
               if (iPlotType.eq.5) then
                  IfF = 0
                  iCC = CC
                  Call Cerchio( X11, Y11, IRT, iCC, IfF, iCC)
               end if
            end if
         end if
   50 CONTINUE
c
      RETURN
      END
