      SUBROUTINE CombRot(B)
      IMPLICIT REAL*4 (M)
      CHARACTER *1 Lett
      CHARACTER *5 Lab
c
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      COMMON /Crt/ iBarre(62, 4),Lett(72),NumBar(70), iCheBarre(70, 6)
     2     , Rmin1, Rmin2, RMAX1, RMAX2, XMax, YMax, DDX, DDY, NPX, NPY
     3     , Lim1,Lim2,Lim3,Lim4,PI,Xmin,Ymin,LegCol,nlp, nMaxLeg
     5     , NumLeg, iPlotType , RK(4), VV(10,3), LE(800,2), RI(3)
      DIMENSION DU(3, 3), VU(10), W(3, 3), B(3,3)
c
         IF (nint(RK(2)) .EQ. 0) GOTO 80
         DO 20 ISS = 1 , 3
            DO 10 kSSI = 1 , 3
               DU(ISS, kSSI) = 0
   10       CONTINUE
   20    CONTINUE
         T = PI / 180 * RK(1)
         DSINT = SIN(T)
         DCOST = COS(T)
         IF (nint(RK(2)) .EQ. 1) THEN
            DU(1, 1) = 1
            DU(2, 2) = DCOST
            DU(3, 3) = DCOST
            DU(2, 3) = -DSINT
            DU(3, 2) = DSINT
         END IF
         IF (nint(RK(2)) .EQ. 2) THEN
            DU(1, 1) = DCOST
            DU(1, 3) = DSINT
            DU(2, 2) = 1
            DU(3, 1) = -DSINT
            DU(3, 3) = DCOST
         END IF
         IF (nint(RK(2)) .EQ. 3) THEN
            DU(1, 1) = DCOST
            DU(1, 2) = -DSINT
            DU(2, 1) = DSINT
            DU(2, 2) = DCOST
            DU(3, 3) = 1
         END IF
         IF (nint(RK(2)) .EQ. 4) THEN
            NRTO = RK(3)
            PAR = VV(NRTO, 1) ** 2 + VV(NRTO, 2) ** 2
            PAR = PAR + VV(NRTO, 3) ** 2
            DDN = SQRT(PAR)
            DO 30 i = 1 , 3
               VU(i) = VV(NRTO, i) / DDN
   30       CONTINUE
            VU(4) = -VU(2)
            VU(5) = VU(1)
            ANG = RK(1)
            RO = PI / 180 * ANG
            CORO = COS(RO)
            SIRO = SIN(RO)
            UCORO = 1 - CORO
            DO 50 i = 1 , 3
               DO 40 j = i , 3
                  DU(i, j) = VU(i) * VU(j) * UCORO
                  IF (i.EQ.j) THEN
                     DU(i, j) = DU(i, j) + CORO
                  ELSE
                     DU(j, i) = DU(i, j) + VU(i + j) * SIRO
                     DU(i, j) = DU(i, j) - VU(i + j) * SIRO
                  END IF
   40          CONTINUE
   50       CONTINUE
         END IF
 
         CALL Molt2(B, DU, W)
 
         DO 70 i = 1 , 3
            DO 60 j = 1 , 3
               B(i, j) = W(i, j)
   60       CONTINUE
   70    CONTINUE
   80 CONTINUE
c
      RETURN
      END
