         LE(nMaxLeg - nlp, 1) = iu
         LE(nMaxLeg - nlp, 2) = NAT + nv
         iu = NAT + nv
         nlp = nlp + 1
         r1 = r1 + r0
         nv = nv +1
         if (NAT + nv .gt. nMaxAtm) goto 400
         if (r1 .lt. 1.) then
             goto 250
         else
             LE(nMaxLeg - nlp, 1) = iu
             LE(nMaxLeg - nlp, 2) = k
             nlp = nlp + 1
         end if
  300 continue
  400 LE(nMaxLeg - nlp, 1) = 0
      LE(nMaxLeg - nlp, 2) = 0
c
      return
      end
