      common/xwi/ color,xo,yo,khead,Species,NumSpec,iHCopy,ipres,jpres
      integer *2 color, xo, yo, khead
      INTEGER *4  CC, CR
      DIMENSION EL(3, 3), C(3), RJ(3)
      DIMENSION H(128, 3)
      character*80 string, Species(8)*2
      integer X11, Y11, R
c
      ifQ =0
