         iiY = Y11 + 2 * fy
         L = Lung(Lab(IJ))
         IF (L .GT. 0) THEN
            iiX = X11 - fx * 6.0 * L / 2.0
            IColor = 7 !white
            CALL LabelM(Lab(IJ), iiX, iiY, fx , fy , IColor)
         END IF
      END IF
