  500 DTOR= PI / 180.
      N2 = 40
      AngSt = -225. * DTOR
      AngEn = 135. * DTOR
      DeAng= 360. * DTOR / N2
      Ang = AngSt - DeAng
      do 10 i=1, N2
         Ang = Ang + DeAng
         if (iHCopy.eq.0) then
            ixv(i) = nint( X11 + R * COS(Ang) )
         else
            ixv(i) = nint( X11 + 2.* R * COS(Ang) )
         end if
         iyv(i) = nint( Y11 - R * SIN(Ang) )
   10 continue
*     ixv(N2 + 1) = ixv(1)
*     iyv(N2 + 1) = iyv(1)
*     nz = N2 + 1
*     ind = 13
*     iot = 8
*     call graphs(ixv, iyv, nz, ind,string, iot)
      nz = N2
      ind = 14
      iot = CR
      call graphs(ixv, iyv, nz, ind,string, iot)
 
      DO 50 K = 1 , 3
         DO 40 L = 1 , 3
            IF (L.NE.K) THEN
               EL(K, L) = 0
            ELSE
               EL(K, L) = 1. / FLOAT(R * R)
            END IF
   40    CONTINUE
         C(K) = EL(K, 3)
   50 CONTINUE
      RJ(1) = 1
      RJ(2) = -1
      RJ(3) = .7
      CE = 1
      RANA1 = X11
      RANA2 = Y11
C
C  richiamo sub EllPrinc
C
      iColor=0
      N1 = 9
      CALL EllPrinc(RJ, C, EL, N1, H)
      N2M = N2 / 2
      do 60 i=1, N1
         if (iHCopy.eq.0) then
            ixv(N2M + i) = H(i, 1) + X11
         else
            ixv(N2M + i) = 2. * H(i, 1) + X11
         end if
         iyv(N2M + i) = H(i, 2) + Y11
   60 continue
      nz = N2M + N1 - 1
      ixv(nz)=ixv(1)
      iyv(nz)=iyv(1)
      ind = 13
      iot = CC
      call graphs(ixv, iyv, nz, ind,string, iot)
c-- fill !!!
      ind = 14
      call graphs(ixv, iyv, nz, ind,string, iot)
c
         if (iHCopy.eq.0) then
            iCC = 15
         else
            iCC = 0
         end if
         iPieno = 1
         iX11=nint(X11+0.5*R)
         iY11=nint(Y11-0.5*R)
         Call Cerchio( iX11, iY11, iPP, iCC, iPieno, iCC)
c
      RETURN
