      SUBROUTINE EllPrinc(RJ, C, EL, N1, H)
      IMPLICIT REAL*4 (M)
      CHARACTER *1 Lett
      CHARACTER *5 Lab
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      COMMON /Crt/ iBarre(62, 4),Lett(72),NumBar(70), iCheBarre(70, 6)
     2     , Rmin1, Rmin2, RMAX1, RMAX2, XMax, YMax, DDX, DDY, NPX, NPY
     3     , Lim1,Lim2,Lim3,Lim4,PI,Xmin,Ymin,LegCol,nlp, nMaxLeg
     5     , NumLeg, iPlotType , RK(4), VV(10,3), LE(800,2), RI(3)
      DIMENSION S(3), G(3), E(3), H(128, 3), RJ(3), EL(3,3), C(3)
c
      S(1) = C(2) * RJ(3) - C(3) * RJ(2)
      S(2) = C(3) * RJ(1) - C(1) * RJ(3)
      S(3) = C(1) * RJ(2) - C(2) * RJ(1)
      CALL NormVett(S, EL, Q)
      DO 10 i = 1 , 3
         G(i) = Q * RI(i)
   10 CONTINUE
      DO 30 i = 1 , 3
         EI = 0
         DO 20 j = 1 , 3
            EI = EI + EL(i, j) * G(j)
   20    CONTINUE
         E(i) = EI
   30 CONTINUE
      S(1) = RJ(2) * E(3) - RJ(3) * E(2)
      S(2) = RJ(3) * E(1) - RJ(1) * E(3)
      S(3) = RJ(1) * E(2) - RJ(2) * E(1)
      CALL NormVett(S, EL, Q)
      DO 40 i = 1 , 3
         E(i) = Q * RI(i)
   40 CONTINUE
      IF (E(3) .LE. 0) THEN
                         E(1) = -E(1)
                         E(2) = -E(2)
                         E(3) = -E(3)
                       END IF
      N1 = 9
      N2 = 2
      N = 4
      nP1 = N + 1
      N2P1 = N * 2 + 1
      DO 50 j = 1 , 3
         H(1, j) = 1. * G(j)
         H(nP1, j) = E(j)
         H(N2P1, j) = -1. * G(j)
   50 CONTINUE
      DO 90 M = 1 , N2
         N1N = N1 - N
         DO 80 JU = 1 , N1N , N
            K = JU + N
            DO 60 L = 1 , 3
               S(L) = H(JU, L) + H(K, L)
   60       CONTINUE
            CALL NormVett(S, EL, Q)
            DO 70 i = 1 , 3
               E(i) = Q * RI(i)
   70       CONTINUE
            JJ = JU + N / 2
            H(JJ, 1) = E(1)
            H(JJ, 2) = E(2)
            H(JJ, 3) = E(3)
   80    CONTINUE
         N = N / 2
   90 CONTINUE
c     XX = H(1, 1) + RANA1
c     YY = H(1, 2) + RANA2
c     DO 100 j = 2 , N1
c        XXX = H(j, 1) + RANA1
c        YYY = H(j, 2) + RANA2
c        CALL Linea (XX, YY, XXX, YYY, 14)
c        XX = XXX
c        YY = YYY
c 100 CONTINUE
c
      RETURN
      END
