      DIMENSION B(3,3), DU(3,3), W(3,3)
c
      DO 30 i = 1 , 3
         DO 20 j = 1 , 3
            dum = 0
            DO 10 K = 1 , 3
               dum = dum + B(i, K) * DU(K, j)
   10       CONTINUE
            W(i, j) = dum
   20    CONTINUE
   30 CONTINUE
c
      RETURN
      END
