      SUBROUTINE SticksOrd
c
      IMPLICIT REAL*4 (M)
      integer  nz, ind, djwc
      integer ixv(100),iyv(100)
      INTEGER O1,O2
      INTEGER *4 CC, CC1, XX1, YY1, XX2, YY2, DDX, DDY, DDDX, DDDY
