      CHARACTER *1 CTMP
      CHARACTER *1 Lett
      CHARACTER *5 Lab
      CHARACTER *80 Titolo
c
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      COMMON /Crt/ iBarre(62, 4),Lett(72),NumBar(70), iCheBarre(70, 6)
     2     , Rmin1, Rmin2, RMAX1, RMAX2, XMax, YMax, DDX, DDY, NPX, NPY
     3     , Lim1,Lim2,Lim3,Lim4,PI,Xmin,Ymin,LegCol,nlp, nMaxLeg
     5     , NumLeg, iPlotType , RK(4), VV(10,3), LE(800,2), RI(3)
      COMMON /Contr/ NF,iFirstTime,Titolo,Ratio,IfCont,IfEdge,IfScale,
     *               kplty,radii(8), IfScaleB
      common/unit/ln,lo,ifour,jrel,iscra,jhost,itle(20),jgrap
c
      DO 20 i = 1 , (NAT + nv) - 1
         DO 10 K = i + 1 , NAT + nv
            O1 = iOrd(i)
            O2 = iOrd(K)
            O3 = xyz(3,O1)
            O4 = xyz(3,O2)
            IF (O3.GT.O4) THEN
               iOrd(i) = O2
               iOrd(K) = O1
            END IF
   10    CONTINUE
   20 CONTINUE
      DDY = 1
      DDX = 1
c
      CALL NewPage
c
      DO 40 JJ = 1 , NAT + nv
         IJ = iOrd(JJ)
         if (RS(IJ).LE.0) go to 40
         if (IfEdge.eq.0 .AND. IJ.gt.NAT) GOTO 40
         ZETA = xyz(3,IJ)
         Legato = 0
         DO 30 NLL = 1 , NumLeg
            N1 = LE(NLL, 1)
            N2 = LE(NLL, 2)
            IF (N1.NE.IJ.AND.N2.NE.IJ) GOTO 30
            IF (N1 .EQ. IJ .AND. xyz(3,N2) .GT. ZETA)  GOTO 30
            IF (N2 .EQ. IJ .AND. xyz(3,N1) .GT. ZETA)  GOTO 30
            IF (RS(N1) .LE. 0 .OR. RS(N2) .LE. 0)  GOTO 30
            Legato = -1
            XX1 = ipix(1,N1)
            XX2 = ipix(1,N2)
            YY1 = ipix(2,N1)
            YY2 = ipix(2,N2)
            ZZ1 = xyz(3,N1)
            ZZ2 = xyz(3,N2)
c           write(lo,'(6f10.4,6i5)') xyz(1,n1),xyz(2,n1),xyz(3,n1),
c    * xyz(1,n2),xyz(2,n2),xyz(3,n2),ipix(1,n1),ipix(2,n1),ipix(3,n1),
c    * ipix(1,n2),ipix(2,n2),ipix(3,n2)
            IF (XX1.LT.XX2) THEN
               DDDX = DDX
            ELSE
               DDDX = -DDX
            END IF
            IF (YY1.LT.YY2) THEN
               DDDY = DDY
            ELSE
               DDDY = -DDY
            END IF
c            XD1= XX1 + DDDX
c            XD2= XX2 - DDDX
cdjwMay-2011 draw bond border
            x1p=xx1+dddx
            x1m=xx1-dddx
            y1p=yy1+dddy
            y1m=yy1-dddy
            x2p=xx2+dddx
            x2m=xx2-dddx
            y2p=yy2+dddy
            y2m=yy2-dddy
c mid-points 
            xbp=(x1p+x2p)/2
            ybp=(y1p+y2p)/2
            xbm=(x1m+x2m)/2
            ybm=(y1m+y2m)/2
c
            ixv(1)=x1p
            iyv(1)=y1p
c
            ixv(2)=x1m
            iyv(2)=y1m
c
            ixv(3)=xbm
            iyv(3)=ybm
c
            ixv(4)=xbp
            iyv(4)=ybp
c
            ind = 14
            ctmp = ' '
            nz = 4
            cc1 = iCol(N1) + 8
            cc  = iCol(N2) + 8
c      
      call graphs(ixv, iyv, nz, ind, ctmp, cc1)
c
            ixv(1)=xbp
            iyv(1)=ybp
c
            ixv(2)=xbm
            iyv(2)=ybm
c
            ixv(3)=x2m
            iyv(3)=y2m
c
            ixv(4)=x2p
            iyv(4)=y2p
c
      call graphs(ixv, iyv, nz, ind, ctmp, cc)
c black line down the middle - just in case there is a grey bond
            djwc = 0 !black
            if ((cc .ne. 15).and.(cc1.ne.15)) then
                  call Linea (xx1,yy1,xx2,yy2,djwc)  
            endif
c
c
cdjw May 2011 LINEA calls replace by polygon above
c            CALL Linea (XD1, YY1-DDDY, XD2, YY2 - DDDY, Ize)
c            CALL Linea (XD1, YY1+DDDY, XD2, YY2 + DDDY, Ize)
c            CALL Linea (XD1, YY1-2 * DDDY, XD2, YY2 - 2 * DDDY,Ize)
c            CALL Linea (XD1, YY1+2 * DDDY, XD2, YY2 + 2 * DDDY,Ize)
c            XX3 = (XX1 + XX2) / 2
c            YY3 = (YY1 + YY2) / 2
c            CC1 = iCol(N1) + 8
c            CC  = iCol(N2) + 8
c            CALL Linea (XX1, YY1, XX3, YY3, CC1)
c            CALL Linea (XX3, YY3, XX2, YY2, CC)
            IF (iPlotType .EQ. 2 .AND. N1 .LE. NAT) THEN
               iCC = 8
               IfF = -1
               Call Cerchio(XX1, YY1, 3 * DDX, iCC, IfF, iCC)
               iCC = 0
               IfF = 0
               iFC = 0
               Call Cerchio( XX1, YY1, 4 * DDX, iCC, IfF, iFC)
               Call Paint(XX1, YY1, CC1-8, iFC,4*DDx)
               IDum = 15
               CALL MettiPixel(XX1 + DDX, YY1 - 2 * DDY, IDum)
               if (Lab(N1)(1:1).eq.'Q') then
                  IfF = -1
                  iFC = 0
                  Call Cerchio( XX2, YY2, 2 * DDX, iCC, IfF, iFC)
               end if
               if (N2 .LE. NAT) THEN
               iCC = 8
