               IDum = 15
               CALL MettiPixel(XX2 + DDX, YY2 - 2 * DDY, IDum)
               if (Lab(N2)(1:1).eq.'Q') then
                  IfF = -1
                  iFC = 0
                  Call Cerchio( XX2, YY2, 2 * DDX, iCC, IfF, iFC)
