         enddo
      enddo
      if (icent.eq.1) nsym=nsym*2
      end if
c
c
 
      Tra(1, 1) = 1
      Tra(2, 1) = 0
      Tra(3, 1) = 0
      Tra(1, 2) = 0
      Tra(2, 2) = 1
      Tra(3, 2) = 0
      Tra(1, 3) = 0
      Tra(2, 3) = 0
      Tra(3, 3) = 1
 
      DeA = 3.0/cx(1)
      DeB = 3.0/cx(2)
      DeC = 3.0/cx(3)
      if ( DeA .gt. cx(1)*0.5 ) DeA = cx(1) * 0.5
      if ( DeB .gt. cx(2)*0.5 ) DeB = cx(2) * 0.5
      if ( DeC .gt. cx(3)*0.5 ) DeC = cx(3) * 0.5
 
      DePA = 1.0 + DeA
      DeMA =     - DeA
      DePB = 1.0 + DeB
      DeMB =     - DeB
      DePC = 1.0 + DeC
      DeMC =     - DeC
 
      iCentrato = 0
      nce = 0
      nit = 3
      do 50 i = 1, NAT
         xyz(1, i) = x(1,i)
         xyz(2, i) = x(2,i)
         xyz(3, i) = x(3,i)
         iEqui(i) = i
   50 continue
c
      IF (nt1 .NE. 'p') THEN
         CALL Centra(nce, Shf, nt1)
         iCentrato = -1
      END IF
 
      natot = NAT
      do 200 na = 1, NAT
         do 150 n = 1, nsym
            CALL Ruota(R, T, n, na, cb)
            idop = iDoppio(natot, cb)
            IF (idop .EQ. 0) THEN
               natot = natot + 1
               xyz(1, natot) = cb(1)
               xyz(2, natot) = cb(2)
               xyz(3, natot) = cb(3)
               iEqui(natot) = na
               if (natot.eq.nMaxAtm - nv) go to 480
            END IF
  150    continue
  200 continue
 
      NAT = natot
      IF (iCentrato .NE. 0) THEN
         do 250 na = 1, NAT
            do 220 n =1, nce
               CALL Trasl(Shf, n, na, cb, -1)
               idop = iDoppio(natot, cb)
               IF (idop .EQ. 0) THEN
                  natot = natot + 1
                  xyz(1, natot) = cb(1)
                  xyz(2, natot) = cb(2)
                  xyz(3, natot) = cb(3)
                  iEqui(natot) = iEqui(na)
                  if (natot.eq.nMaxAtm - nv) go to 480
               END IF
  220       continue
  250    continue
      END IF
 
      NAT = natot
      do 450 it = 1, nit
         do 400 ic = -1, 1, 2
            do 350 na = 1 , NAT
               do 300 i = 1, 3
                  Tra(i, it) = ic * ABS(Tra(i, it))
  300          continue
               CALL Trasl(Tra, it, na, cb, -1)
               idop = iDoppio(natot, cb)
               IF (idop .EQ. 0) THEN
                  natot = natot + 1
                  xyz(1, natot) = cb(1)
                  xyz(2, natot) = cb(2)
                  xyz(3, natot) = cb(3)
                  iEqui(natot) = iEqui(na)
                  if (natot.eq.nMaxAtm - nv) go to 480
               END IF
  350       continue
 
            NAT = natot
 
  400    continue
  450 continue
 
  480 NAT = natot
 
c     io=2
c     open(unit=2,status='unknown',file='Compl.mol')
c     write(io,'(a)') titolo
c     write(io,'(6f10.6)') a, b, c, alfa, beta, gamma
c     write(io,'(i5)') NAT
 
      do 550 na = 1, NAT
         ieq = iEqui(na)
         Lab(na) = Lab(ieq)
         MLU(na) = MLU(ieq)
         RS(na) = RS(ieq)
         iCol(na) = iCol(ieq)
         Lab(na) = Lab(ieq)
c     write(io,'(a4,1h,,5f10.6,i4)')Lab(na),(xyz(i,na),i=1,3),
c    +                                MLU(na),RS(na),iCol(na)
         x1(1) = xyz(1, na)
         x1(2) = xyz(2, na)
         x1(3) = xyz(3, na)
         call molt1(rm,x1,r1)
         xyz(1, na) = r1(1)
         xyz(2, na) = r1(2)
         xyz(3, na) = r1(3)
  550 continue
      call AggiuSpig
c
      call LsPlane(NATV)
c
      call BuildBonds(2)
c
      Rmin1 = 99999.
      Rmin2 = 99999.
      RMAX1 = -99999.
      RMAX2 = -99999.
c
      CALL FindOr(B)
c
      RETURN
      END
c --------------------------------------------------------------------
      SUBROUTINE Centra (nce, Shf, nt1)
 
      CHARACTER *1 nt1
      DIMENSION Shf(3,3)
 
      IF (nt1 .EQ. 'f') THEN
         nce = 3
         Shf(1, 1) = 0.
         Shf(2, 1) = .5
         Shf(3, 1) = .5
         Shf(1, 2) = .5
         Shf(2, 2) = 0.
         Shf(3, 2) = .5
         Shf(1, 3) = .5
         Shf(2, 3) = .5
         Shf(3, 3) = 0.
      END IF
      IF (nt1 .EQ. 'r') THEN
         nce = 2
         Shf(1, 1) = .333
         Shf(2, 1) = .666
         Shf(3, 1) = .333
         Shf(1, 2) = .666
         Shf(2, 2) = .333
         Shf(3, 2) = .666
      END IF
      IF (nt1 .EQ. 'i') THEN
         nce = 1
         Shf(1, 1) = .5
         Shf(2, 1) = .5
         Shf(3, 1) = .5
      END IF
      IF (nt1 .EQ. 'a') THEN
         nce = 1
         Shf(1, 1) = 0.
         Shf(2, 1) = .5
         Shf(3, 1) = .5
      END IF
      IF (nt1 .EQ. 'b') THEN
         nce = 1
         Shf(1, 1) = .5
         Shf(2, 1) = 0.
         Shf(3, 1) = .5
      END IF
      IF (nt1 .EQ. 'c') THEN
         nce = 1
         Shf(1, 1) = .5
         Shf(2, 1) = .5
         Shf(3, 1) = 0.
      END IF
 
      RETURN
      END
c --------------------------------------------------------------------
      FUNCTION iDoppio (natot, c)
 
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      DIMENSION c(3)
      character *5 Lab
      real *4 MLU
 
      iDoppio = 0
      DO 20 nn = 1, natot
         DO 10 i = 1, 3
            IF (ABS(xyz(i, nn) - c(i)) .GT. .001) THEN
                GOTO 20
            END IF
   10    CONTINUE
         iDoppio = -1
         RETURN
   20 CONTINUE
 
      RETURN
      END
c --------------------------------------------------------------------
      SUBROUTINE RimettiDentro (c)
 
      character * 1 nt1
      COMMON /GeneraC/ DePA, DeMA, DePB, DeMB, DePC, DeMC,
     *                 R(3, 3, 48), T(3, 48),
     *                 Shf(3, 3), Tra(3, 20), nsym, NatInd, nt1
      DIMENSION c(3)
 
   10 if (c(1) .gt. DePA) then
         c(1) = c(1) - 1
         goto 10
      end if
   20 if (c(1) .lt. DeMA) then
         c(1) = c(1) + 1
         goto 20
      end if
   30 if (c(2) .gt. DePB) then
         c(2) = c(2) - 1
         goto 30
      end if
   40 if (c(2) .lt. DeMB) then
         c(2) = c(2) + 1
         goto 40
      end if
   50 if (c(3) .gt. DePC) then
         c(3) = c(3) - 1
         goto 50
      end if
   60 if (c(3) .lt. DeMC) then
         c(3) = c(3) + 1
         goto 60
      end if
 
      RETURN
      END
c --------------------------------------------------------------------
      SUBROUTINE Ruota (R, T, n, na, cb)
 
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      DIMENSION A(3, 3), B(3), R(3,3,48), T(3,48), cb(3)
      character *5 Lab
      real *4 MLU
 
      DO 20 j = 1, 3
         DO 10 k = 1, 3
            A(k, j) = R(k, j, n)
  10     CONTINUE
         B(j) = xyz(j, na)
  20  CONTINUE
      DO 40 i = 1, 3
         cb(i) = 0
         DO 30 j = 1, 3
            cb(i) = cb(i) + A(i, j) * B(j)
  30     CONTINUE
         cb(i) = cb(i) + T(i, n)
  40  CONTINUE
 
      CALL RimettiDentro(cb)
 
      RETURN
      END
c --------------------------------------------------------------------
      SUBROUTINE Trasl (T, n, na, cb, ird)
 
      COMMON /atomi/ xyz(3,800),NAT, RR(800), iCol(800), iOrd(800),nv,
     *               MLU(800), Lab(800), RS(800), ipix(3,800),iEqui(800)
      DIMENSION T(3,3), cb(3)
      character *5 Lab
      real *4 MLU
 
      DO 10 i = 1, 3
       cb(i) = xyz(i, na) + T(i, n)
   10 CONTINUE
      IF (ird .NE. 0) THEN
       CALL RimettiDentro(cb)
      END IF
      RETURN
      END
