  555                if (mod(ini-1,3).ne.0) then
                        ini = ini+1
                        goto 555
                     end if
CCC                  if (ini.gt.NatInd) then
                     if (ini.gt.npointer) then
                                          iz = -1 
                                          goto 222
                                        endif
                  end if
                  call NewPage
                  iyp = iyp0
                  goto 15
               end if
            end if
         end if
   20 continue
      return
      end
