      SUBROUTINE Traslate (iAction)
c
      CHARACTER *1 Lett
      COMMON /Crt/ iBarre(62, 4),Lett(72),NumBar(70), iCheBarre(70, 6)
     2     , Rmin1, Rmin2, RMAX1, RMAX2, XMax, YMax, DDX, DDY, NPX, NPY
     3     , Lim1,Lim2,Lim3,Lim4,PI,Xmin,Ymin,LegCol,nlp, nMaxLeg
     5     , NumLeg, iPlotType , RK(4), VV(10,3), LE(800,2), RI(3)
c
      Rmin1 = Xmin
      Rmin2 = Ymin
      RMax1 = XMax
      RMax2 = YMax
      D1 = (RMAX1 - Rmin1) / 10
      D2 = (RMAX2 - Rmin2) / 10
      IF (iAction.EQ.13) then
         Rmin1 = Rmin1 + D1
         RMax1 = RMax1 + D1
      else IF (iAction.EQ.14) then
         Rmin1 = Rmin1 - D1
         RMax1 = RMax1 - D1
      else IF (iAction.EQ.15) then
         Rmin2 = Rmin2 + D2
         RMax2 = RMax2 + D2
      else IF (iAction.EQ.16) then
         Rmin2 = Rmin2 - D2
         RMax2 = RMax2 - D2
      else IF (iAction.EQ.17) then
         Rmin1 = Rmin1 - D1
         RMax1 = RMax1 + D1
         Rmin2 = Rmin2 - D2
         RMax2 = RMax2 + D1
      else IF (iAction.EQ.18) then
         Rmin1 = Rmin1 + D1
         RMax1 = RMax1 - D1
         Rmin2 = Rmin2 + D2
         RMax2 = RMax2 - D2
      end if
      Xmin = Rmin1
      Ymin = Rmin2
      XMax = RMax1
      YMax = RMax2
      CALL SetScale(3)
      RETURN
      END
