C --------------------------------------------------------------------
      FUNCTION Interno (ix, iy, nv)
      COMMON /buffer/ ixv(100), iyv(100),dummr(315)
      real  m
c
      NumIntD = 0
      NumIntS = 0
      Inter = 0
      do 10 k = 1, nv - 1
          ia = iy - iyv(k + 1)
          IF (ia .ne. 0) THEN
              a1 = iy - iyv(k + 1)
              b1 = iyv(k) - iy
              IF (b1 / a1 .ge. 0.0) THEN
                  IF (iyv(k + 1) .ne. iyv(k)) THEN
                      a = ixv(k + 1) - ixv(k)
                      b = iyv(k + 1) - iyv(k)
                      IF (ABS(b) .gt. .00001) THEN
                          m = a / b
                      ELSE
                          m = 0
                      END IF
                      rix1 = ixv(k) + (iy - iyv(k)) * m
                  ELSE
                      rix1 = ixv(k)
                  END IF
                  IF (rix1 .lt. ix) THEN
                      NumIntS = NumIntS + 1
                  END IF
                  IF (rix1 .gt. ix) THEN
                      NumIntD = NumIntD + 1
                  END IF
              END IF
          END IF
10    continue
      IF (NumIntD .gt. 0) THEN
         IF (INT(NumIntD / 2) * 2 .ne. NumIntD) THEN
            Inter = -1
         END IF
      END IF
      IF (NumIntS .gt. 0) THEN
         IF (INT(NumIntS / 2) * 2 .ne. NumIntS) THEN
            Inter = Inter - 1
         END IF
      END IF
      Interno = Inter
c
      return
      END
