      subroutine laue(space,jsys,laues)
      character space*16,laues*5,string*16,dire*16
c
      if (jsys.le.3) then
                       if (jsys.eq.1) then
                                        laues=' -1  '
                  else if (jsys.eq.2) then
                                        laues=' 2/m '
                  else if (jsys.eq.3) then
                                        laues=' mmm '
                                      endif
                     else
                       string=space 
                       call cutst(string,lens,dire,lend)
c--calcola il numero di operatori
                       nop=1
                       do 40 i=1,3
                       call cutst(string,lens,dire,lend)
                       if (lens.le.0) go to 45
                       nop=nop+1
   40                  continue 
   45                  continue 
           if (jsys.eq.4) then
                if (nop.eq.1) then
                                laues=' 4/m '
                              else 
                                laues='4/mmm'
                              endif
       else if (jsys.eq.5) then
                 j=index(space,'3')
                 if (j.eq.0) then
                  if (nop.eq.1) then
                                   laues=' 6/m '
                                 else 
                                   laues='6/mmm'
                                 endif
                              else 
                     if (nop.eq.1) then
                                      laues='  -3 '
                                    else  
                                      laues=' -3m '
                                    endif 
                                endif
        else          
             if (nop.eq.2) then
                             laues=' m-3 '
                           else 
                            laues=' m-3m'
                           endif
                       endif
                       endif
        return
        end  
