      alpham=25.
      go to 2990
 3524 continue
c  considero tutti i riflessi e faccio il controllo su sumalf
 3525 if (salf.le.0.0001) go to 9001
      if((salf-sumalf)/salf-0.02) 9001,9001,2990
 9001 if(ifin.eq.1) go to 3530
      do 3528 ll=1,numb
