      if (nmax.gt.1) go to 100
      if (nmax.le.0) write(lo,110)
      num(1)=1
      iflag=1
      return
  100 continue
      do 10 i=1,nmax
      num(i)=i
   10 continue
      k=1
    1 k=2*k
      if (k.lt.nmax) go to 1
      k=k/2
    7 l=nmax-k
      j=1
    6 if (j.gt.l) go to 2
      i=j
    5 ik=i+k
      m=i
    4 x=v(m)
      mj=m+k
      y=v(mj)
      if (x.ge.y) go to 3
      com=num(m)
      num(m)=num(mj)
      num(mj)=com
      com=v(m)
      v(m)=v(mj)
      v(mj)=com
      m=m-k
      i=ik
      if (m.gt.0) go to 4
    3 i=i+k
      if (i.le.l) go to 5
      j=j+1
      if (j.le.k) go to 6
    2 k=k/2
      if (k.gt.0) go to 7
      return
