CODE FOR XNEGMT
      SUBROUTINE XNEGMT(A,M,N)
C--NEGATE ALL ELEMENTS OF A MATRIX
      DIMENSION A(M,N)
      DO I=1,N
            DO J=1,M
                  A(M,N)=-A(M,N)
            ENDDO
      ENDDO
      END
