CODE FOR FDPROD
      FUNCTION FDPROD(A,B) 
C----- THE SUBROUTINE DVPROD PACKAGED AS A FUNCION
C      FOR USE IN EXPRESSIONS
C----- RETURN VALUE IS THE PRODUCT.
C 
      DIMENSION A(3), B(3)
      CALL DVPROD(A,B,C)
      FDPROD = C
      RETURN
      END
